INSERT INTO `article` VALUES ('f4694a36-6fbb-47e5-87dd-d8bb07b2cac8', '数计数学与计算机科学学院开展入党积极分子拟发展对象答辩大会', '2016-05-31', '<p class=\"MsoNormal\" align=\"left\" style=\"font-size:10.5pt;font-family:Calibri, sans-serif;text-indent:28pt;\">\r\n	<span style=\"font-size:14pt;font-family:宋体;\">为考察党员拟发展对象的政治素质和党性修养，端正入党动机、提高政治觉悟。<span style=\"font-size:18.6667px;\">2016</span>年<span style=\"font-size:18.6667px;\">5</span>月<span style=\"font-size:18.6667px;\">31</span>日下午，数学与计算机科学学院在宁静楼第九会议室召开了入党积极分子拟发展对象答辩大会。党委书记肖湘萍、党委答辩委员会全体成员参加了此次答辩会，学生党支部委员会成员列会。</span>\r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"font-size:10.5pt;font-family:Calibri, sans-serif;text-indent:28pt;\">\r\n	<span style=\"font-size:14pt;font-family:宋体;\">答辩会开始前，答辩同学在第九会议室外排队等待答辩。会议开始，答辩同学分别进入第九会议室答辩，答辩同学首先进行<span style=\"font-size:18.6667px;\">2</span>分钟的自我介绍，就个人基本情况、思想动态、如何在行动中积极向党组织靠拢等主题发表个人阐述；其次，由答辩委员会成员就有关党的知识进行提问，答辩同学做相关回答，谈吐中体现出拟发展对象对党的知识的充分理解。拟发展对象回答后，答辩委员会成员给予回应，在表扬答辩同学优点的同时，也对答辩同学的不足提出改进意见，答辩同学认真听取意见。</span>\r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"font-size:10.5pt;font-family:Calibri, sans-serif;text-indent:28pt;\">\r\n	<span style=\"font-size:14pt;font-family:宋体;\">整场答辩会有条不紊地进行。会议最后，答辩委员会的全体人员以无记名投票的方式对各发展对象的答辩情况进行投票，确定最后拟发展对象名单。答辩大会圆满结束。</span>\r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"font-size:10.5pt;font-family:Calibri, sans-serif;text-indent:28pt;\">\r\n	<span style=\"font-size:14pt;font-family:宋体;\">文、图<span style=\"font-size:18.6667px;\">/</span>李红霞<span style=\"font-size:18.6667px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><a name=\"_GoBack\"></a>审核<span style=\"font-size:18.6667px;\">/</span>肖湘萍</span>\r\n</p>\r\n<p class=\"MsoNormal\" align=\"center\" style=\"text-align:center;font-size:10.5pt;font-family:Calibri, sans-serif;text-indent:28pt;\">\r\n	<span style=\"font-size:14pt;font-family:宋体;\"><img id=\"图片 1\" alt=\"答辩现场\" src=\"http://mcs.gznc.edu.cn/images/16/06/02/10ys5t2ed1/ytb6_image001.jpg\" width=\"549\" height=\"414\" /></span>\r\n</p>\r\n<p class=\"MsoNormal\" align=\"center\" style=\"text-align:center;font-size:10.5pt;font-family:Calibri, sans-serif;text-indent:28pt;\">\r\n	<span style=\"font-size:14pt;font-family:宋体;\">答辩现场</span>\r\n</p>\r\n<p class=\"MsoNormal\" align=\"center\" style=\"text-align:center;font-size:10.5pt;font-family:Calibri, sans-serif;text-indent:28pt;\">\r\n	<span style=\"font-size:14pt;font-family:宋体;\"><img id=\"图片 2\" alt=\"党委书记肖湘萍提问答辩同学\" src=\"http://mcs.gznc.edu.cn/images/16/06/02/10ys5t2ed1/ytb6_image002.jpg\" width=\"549\" height=\"414\" /></span>\r\n</p>\r\n<p class=\"MsoNormal\" align=\"center\" style=\"text-align:center;font-size:10.5pt;font-family:Calibri, sans-serif;text-indent:28pt;\">\r\n	<span style=\"font-size:14pt;font-family:宋体;\">党委书记肖湘萍提问答辩同学</span>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:justify;font-size:10.5pt;font-family:Calibri, sans-serif;\">\r\n	<span style=\"font-size:14pt;font-family:宋体;\">&nbsp;</span>\r\n</p>', 'b3b15b7b-2fff-4216-ae1a-e4d59cb1ef88');
