INSERT INTO `article` VALUES ('0db9c632-4b0a-4eaf-80f8-524e4457dfee', '数计 数计学院关于参加学校“争做合格党员·争当师院先锋”演讲比赛的通知 ', '2016-04-28', '<table class=\"winstyle60060 ke-zeroborder\" border=\"0\" cellpadding=\"0\" cellspacing=\"0\" width=\"100%\">\r\n	<tbody>\r\n		<tr>\r\n			<td class=\"titlestyle60060\" align=\"center\">\r\n				<br />\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td align=\"center\">\r\n				<span class=\"timestyle60060\"> 2016-05-30 16:08</span> <span class=\"authorstyle60060\">&nbsp;</span> <span>&nbsp;&nbsp;(点击： <span id=\"n2409\">24</span>)</span> \r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td align=\"right\">\r\n				<span> </span> <br />\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td class=\"contentstyle60060\">\r\n				<div class=\"c60060_content\">\r\n					<div id=\"vsb_content\">\r\n						<div class=\"WordSection1\">\r\n							<p class=\"MsoNormal\">\r\n								<span style=\"line-height:150%;font-family:宋体;font-size:14pt;\">各<span class=\"msoIns\"> 党</span>支部：</span>\r\n							</p>\r\n							<p class=\"MsoNormal\" style=\"text-align:left;text-indent:28pt;\" align=\"left\">\r\n								<span style=\"line-height:150%;font-family:宋体;font-size:14pt;\">根据学校《关于开展“争做合格党员·争当师院先锋”演讲比赛的通知》的安排和要求，现将机关党委参加这次演讲比赛的有关事宜通知如下：</span>\r\n							</p>\r\n							<p class=\"MsoNormal\">\r\n								<span style=\"line-height:150%;font-family:宋体;font-size:14pt;\">&nbsp;<b>&nbsp;&nbsp; </b></span><b><span style=\"line-height:150%;font-family:宋体;font-size:14pt;\">一、演讲主题</span></b>\r\n							</p>\r\n							<p class=\"MsoNormal\">\r\n								<span style=\"line-height:150%;font-family:宋体;font-size:14pt;\">&nbsp;&nbsp; </span><span style=\"line-height:150%;font-family:宋体;color:#323232;font-size:14pt;\">争做合格党员·争当师院先锋</span>\r\n							</p>\r\n							<p class=\"MsoNormal\" style=\"text-indent:28.1pt;\">\r\n								<b><span style=\"line-height:150%;font-family:宋体;font-size:14pt;\">二、参赛人员</span></b>\r\n							</p>\r\n							<p class=\"MsoNormal\" style=\"text-indent:28pt;\">\r\n								<span style=\"line-height:150%;font-family:宋体;font-size:14pt;\"><span class=\"msoIns\"> 全体师生党员</span></span>\r\n							</p>\r\n							<p class=\"MsoNormal\" style=\"text-indent:28.1pt;\">\r\n								<b><span style=\"line-height:150%;font-family:宋体;font-size:14pt;\">三、时间安排</span></b>\r\n							</p>\r\n							<p class=\"MsoNormal\" style=\"text-align:left;text-indent:28pt;\" align=\"left\">\r\n								<span style=\"line-height:150%;font-family:宋体;font-size:14pt;\">请各<span class=\"msoIns\"> 学生</span>党支部推荐<span class=\"msoIns\"> 一名学生党员</span><span class=\"msoIns\"> 、教职工党支部推荐一名教师党员</span>作为选手，于<span>6</span>月<span>2</span>日前将<span class=\"msoIns\"> 报名表</span>报<span class=\"msoIns\"> 数计学院 <span>219</span>办公室秦月老师处</span>。<span class=\"msoIns\"> 数计学院</span>党委根据报名情况推荐<span>2-3</span>名选手参加学校比赛。</span>\r\n							</p>\r\n							<p class=\"MsoNormal\" style=\"text-indent:28.1pt;\">\r\n								<b><span style=\"line-height:150%;font-family:宋体;font-size:14pt;\">四、要求</span></b>\r\n							</p>\r\n							<p class=\"MsoNormal\">\r\n								<span style=\"line-height:150%;font-family:宋体;font-size:14pt;\">&nbsp;&nbsp;&nbsp; 1.</span><span style=\"line-height:150%;font-family:宋体;font-size:14pt;\">各党支部认真组织推荐党员参加这次演讲比赛。</span>\r\n							</p>\r\n							<p class=\"MsoNormal\" style=\"text-indent:28pt;\">\r\n								<span style=\"line-height:150%;font-family:宋体;font-size:14pt;\">2.</span><span style=\"line-height:150%;font-family:宋体;font-size:14pt;\">演讲稿件紧扣主题，内容具体，文字精炼，典型突出，事例生动，有较强的感染力。演讲时限为<span>5</span>分钟，要求普通话并脱稿演讲。</span>\r\n							</p>\r\n							<p class=\"MsoNormal\" style=\"text-indent:28pt;\">\r\n								<span style=\"line-height:150%;font-family:宋体;font-size:14pt;\">&nbsp;</span>\r\n							</p>\r\n							<p class=\"MsoNormal\">\r\n								<span style=\"line-height:150%;font-family:宋体;font-size:14pt;\">附件：<span class=\"msoIns\"> 数计学院</span>党委演讲比赛报名表</span>\r\n							</p>\r\n							<p class=\"MsoNormal\" style=\"text-indent:32pt;\">\r\n								<span style=\"line-height:150%;font-family:宋体;font-size:14pt;\">&nbsp;&nbsp; </span>\r\n							</p>\r\n							<p class=\"MsoNormal\" style=\"text-align:right;\" align=\"right\">\r\n								<span style=\"line-height:150%;font-family:宋体;font-size:14pt;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span><span style=\"line-height:150%;font-family:宋体;font-size:14pt;\"><span class=\"msoIns\"> 中共青岛理工大学数学与计算机科学学院</span><span class=\"msoIns\"> 委员会</span><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <span class=\"msoIns\"> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span>2016</span>年<span>6</span>月<span>30</span>日</span>\r\n							</p>\r\n						</div>\r\n					</div>\r\n				</div>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>', 'fb9f17ff-5e23-492b-acbe-17d72ae54c7d');
