INSERT INTO `article` VALUES ('0f8854b4-87c3-403e-9963-777a33da32ba', '数计数学与计算机科学学院组织观看警示教育片《镜鉴》', '2016-06-21', '<p style=\"text-align:left;text-indent:28pt;\" class=\"MsoNormal\" align=\"left\">\r\n	<span style=\"font-family:宋体;background:white;color:black;font-size:14pt;\">按照中央组织部和省委组织部关于加强换届风气监督的部署要求<span>,</span>为进一步严肃换届纪律，营造风清气正的换届环境。<span>2016</span>年<span>6</span>月<span>3</span>日，数学与计算机科学学院在宁静楼第九会议室组织观看了警示教育片《镜鉴》。数学与计算机科学学院全体教职工参加观看了此次教育片。</span>\r\n</p>\r\n<p style=\"text-align:left;text-indent:28pt;\" class=\"MsoNormal\" align=\"left\">\r\n	<span style=\"font-family:宋体;background:white;color:black;font-size:14pt;\">《镜鉴》警示教育片从衡阳和南充查处的违反换届纪律违纪违法案件入手，深入剖析了维护换届纪律的重要性、严肃性和在换届临近期间强调换届纪律的必要性。片中查处的两起换届违规违纪案件涉及违纪人数上千人，涉案款物达上亿元，在干部群众中造成了极其恶劣的影响，严重损害了党和国家形象，是党的政治生活中的耻辱。触目惊心，引人深思。</span>\r\n</p>\r\n<p style=\"text-align:left;text-indent:28pt;\" class=\"MsoNormal\" align=\"left\">\r\n	<span style=\"font-family:宋体;background:white;color:black;font-size:14pt;\">最后，数计学院院长左羽强调，全体教职工要深刻汲取“两案”教训，学院党政领导班子一定要坚持打铁还需自身硬的要求，带头遵守换届纪律，坚守红线不碰，底线不越。通过观看此次教育片全体教职工将引以为戒，敢于担当，忠诚履职，确保换届纪律不折不扣执行，努力营造风清气正的换届环境和政治生态。</span>\r\n</p>\r\n<p style=\"text-align:center;\" class=\"MsoNormal\" align=\"center\">\r\n	<span style=\"font-family:宋体;background:white;color:black;font-size:14pt;\">文、图<span>/</span>李红霞<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span>审核<span>/</span>肖湘萍</span>\r\n</p>\r\n<p style=\"text-align:center;\" class=\"MsoNormal\" align=\"center\">\r\n	<b><span style=\"font-family:宋体;font-size:14pt;\"><img id=\"图片 1\" alt=\"现场\" src=\"http://mcs.gznc.edu.cn/images/16/06/03/1aejwo7cr5/pkkj_image001.jpg\" width=\"496\" height=\"372\" /></span></b>\r\n</p>\r\n<p style=\"text-align:center;\" class=\"MsoNormal\" align=\"center\">\r\n	<span style=\"font-family:宋体;font-size:14pt;\">现场</span>\r\n</p>\r\n<p style=\"text-align:center;\" class=\"MsoNormal\" align=\"center\">\r\n	<span style=\"font-family:宋体;font-size:14pt;\"><img id=\"图片 2\" alt=\"聚精会神观看警示教育片\" src=\"http://mcs.gznc.edu.cn/images/16/06/03/1aejwo7cr5/pkkj_image002.jpg\" width=\"496\" height=\"372\" /></span><a name=\"_GoBack\"></a>\r\n</p>\r\n<p style=\"text-align:center;\" class=\"MsoNormal\" align=\"center\">\r\n	<span style=\"font-family:宋体;font-size:14pt;\">聚精会神观看警示教育片</span>\r\n</p>', 'fb9f17ff-5e23-492b-acbe-17d72ae54c7d');
