INSERT INTO `article` VALUES ('a484a3e7-28b9-4dc7-ab2a-0b0f9854835c', '数计老象学院加油', '2016-06-13', '<table border=\"0\" class=\"winstyle60060 ke-zeroborder\" width=\"100%\" cellspacing=\"0\" cellpadding=\"0\" style=\"font-size:9pt;font-family:宋体;color:#000000;\">\r\n	<tbody>\r\n		<tr>\r\n			<td align=\"center\">\r\n				<span class=\"timestyle60060\" style=\"font-size:9pt;\">2016-05-27 16:27</span>&nbsp;<span class=\"authorstyle60060\" style=\"font-size:9pt;\">&nbsp;</span>&nbsp;&nbsp;&nbsp;(点击：&nbsp;<span id=\"n2405\">12</span>)\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td align=\"right\">\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td class=\"contentstyle60060\" style=\"font-size:9pt;\">\r\n				<div class=\"c60060_content\">\r\n					<div id=\"vsb_content\">\r\n						<div class=\"WordSection1\">\r\n							<p class=\"MsoNormal\" align=\"left\" style=\"font-size:10.5pt;font-family:Calibri, sans-serif;\">\r\n								<span style=\"font-size:14pt;font-family:宋体;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\"font-size:14pt;font-family:宋体;\">为进一步加强干部作风建设，使党员、干部树立正确的权力观、地位观和利益观，筑牢“防腐墙”。<span style=\"font-size:18.6667px;\">2016</span>年<span style=\"font-size:18.6667px;\">5</span>月<span style=\"font-size:18.6667px;\">27</span>日下午，数学与计算机科学学院在宁静楼第九会议室开展了传达有关换届纪律工作会。此次工作会由数学与计算机科学学院党委书记肖湘萍主持，数学与计算机科学学院全体教职工参加了此次工作会。</span>\r\n							</p>\r\n							<p class=\"MsoNormal\" align=\"left\" style=\"font-size:10.5pt;font-family:Calibri, sans-serif;text-indent:28pt;\">\r\n								<span style=\"font-size:14pt;font-family:宋体;\">会上，首先数学与计算机科学学院党委书记肖湘萍传达了学校《关于认真做好换届纪律宣传教育工作的通知》精神，向全体教职工原原本本传达了中央纪委机关、中央组织部《关于加强换届风气监督的通知》（中组发〔<span style=\"font-size:18.6667px;\">2016</span>〕<span style=\"font-size:18.6667px;\">1</span>号）精神；接着学院党委给每位教职工发放“九严禁”换届纪律宣传卡和《给全体教职工的函》，并组织教职工填写《回执》。</span>\r\n							</p>\r\n							<p class=\"MsoNormal\" align=\"left\" style=\"font-size:10.5pt;font-family:Calibri, sans-serif;text-indent:28pt;\">\r\n								<span style=\"font-size:14pt;font-family:宋体;\">最后，肖湘萍强调全体教职工一定要充分认识、高度重视加强换届纪律学习宣传的重要性和必要性，坚决按照学校的通知要求，认真学习宣传卡上的内容、遵守换届纪律、增强纪律观念、筑牢纪律防线、严守纪律规定、坚决维护换届工作的严肃性。</span>\r\n							</p>\r\n						</div>\r\n					</div>\r\n				</div>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>', 'd6991d32-f95b-429f-9313-888109be98fe');
INSERT INTO `article` VALUES ('03f0f644-ec27-4524-bc2f-590d2979218b', '数计加多宝外逃董事长现身为企业站台 检方称仍在追逃', '2016-03-24', '<div class=\"original-tit\" style=\"border:0px;margin:0px;padding:0px 0px 0px 28px;font-size:14px;color:#333333;font-family:宋体, simsun, sans-serif, Arial;font-style:normal;font-weight:normal;text-align:start;background-color:#FFFFFF;\">\r\n	原标题：加多宝外逃董事长陈鸿道高调现身为企业站台 检方称仍在追逃\r\n</div>\r\n<span style=\"color:#333333;font-family:宋体, simsun, sans-serif, Arial;font-size:12px;font-style:normal;font-weight:normal;line-height:14px;background-color:#FFFFFF;\"></span><span style=\"color:#333333;font-family:宋体, simsun, sans-serif, Arial;font-size:12px;font-style:normal;font-weight:normal;line-height:14px;background-color:#FFFFFF;\"></span>\r\n<div class=\"text clear\" id=\"contentText\" style=\"border:0px;margin:0px auto 24px;padding:0px;font-size:14px;color:#333333;font-family:宋体, simsun, sans-serif, Arial;font-style:normal;font-weight:normal;text-align:start;background-color:#FFFFFF;\">\r\n	<div style=\"border:0px;margin:0px;padding:0px;font-size:14px;\">\r\n		<div style=\"border:0px;margin:0px;padding:0px;font-size:14px;\" align=\"center\">\r\n			<img src=\"http://photocdn.sohu.com/20160612/Img453989460.jpeg\" alt=\"图片来源：视觉中国\" class=\"flag_bigP\" style=\"border:0px;\" align=\"middle\" border=\"1\" />\r\n			<div class=\"conserve-photo\" style=\"border:0px;margin:0px;padding:0px;font-size:14px;background:url(http://news.sohu.com/upload/article/2012/images/saveBtn.png) no-repeat;\">\r\n				<div class=\"comCount\" style=\"border:0px;margin:0px;padding:0px;font-size:11px;font-style:normal;font-weight:normal;font-family:宋体;color:white;\">\r\n					1\r\n				</div>\r\n			</div>\r\n		</div>\r\n		<div style=\"border:0px;margin:0px;padding:0px;font-size:14px;\" align=\"center\">\r\n			<span style=\"font-size:12px;\">图片来源：视觉中国</span>\r\n		</div>\r\n		<p style=\"font-size:14px;color:#000000;\">\r\n			&emsp;&emsp;近日，加多宝集团和北京控股集团在香港签署战略合作框架协议。加多宝集团弃保外逃香港十多年的集团董事长陈鸿道首次公开现身，为合作仪式站台，引发热议。对此，广州检察院宣传处刘韬表示，对于陈鸿道的追捕，广州市检察院从未停止。“对于他此次公开现身香港，我们将继续追捕”。\r\n		</p>\r\n		<p style=\"font-size:14px;color:#000000;\">\r\n			&emsp;&emsp;而加多宝方面则拒绝对此事进行表态。\r\n		</p>\r\n		<p style=\"font-size:14px;color:#000000;\">\r\n			&emsp;&emsp;据了解，陈鸿道外逃案可追溯到1995年，当时王老吉药业的前身羊城药业与加多宝集团前身鸿道集团正式签约，将红罐和红瓶王老吉的生产经营权授予鸿道集团；双方于2000年再次签订商标许可主合同，将商标的使用期限延长至2010年。\r\n		</p>\r\n	</div>\r\n</div>', 'b211031a-fb81-4255-985e-0b8d95aa5d65');
